package Library;

public class Restaurant {

    private String name;
    private int stars;
    private String priceCategory;
    private Review review;


    public Restaurant(String name ,int stars, String priceCategory, Review review) {
        this.name=name;
        this.stars=stars;
        this.priceCategory=priceCategory;
        this.review=review;

    }
    public Restaurant(String name ,int stars, String priceCategory) {
        this.name=name;
        this.stars=stars;
        this.priceCategory=priceCategory;

    }


    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public int getStarsNum() {
        return stars;
    }

    public void setStarsNum(int starsNum) {
        this.stars = starsNum;
    }

    public String getPriceCategory() {
        return priceCategory;
    }

    public void setPriceCategory(String priceCategory) {
        this.priceCategory = priceCategory;
    }

    public String getReview() {
        return review.toString();
    }

    public void addReview(String body, String author, int starsNum ) {
        this.review= new Review(body,author,starsNum);
        this.stars= starsNum;
    }



    @Override
    public String toString() {
        return "Restaurant Details{" +
                "Restaurant Name='" + name + '\'' +
                ", Number of stars=" + stars +
                ", Price Category='" + priceCategory + '\'' +
                '}';
    }

}
